import React, { Component } from 'react';

export default class needs extends Component {
  constructor(props) {
    super(props);
    this.state = {
      input: ['input-0']
    };
  }

  clickAdd = (event) => {
    let newInput = `input-${this.state.input.length}`;
    this.setState((prevState) => ({ input: prevState.input.concat([newInput]) }));
  };

  handleInputChange = ({ target: { name, value } }) => {
    this.setState({
      [name]: value
    });
  };

  submitHandle = (event) => {
    event.preventDefault;
  };

  render() {
    return (
      <form onSubmit={this.submitHandle}>
        <label>{this.props.type}</label>
        <button onClick={() => this.clickAdd()}>Add</button>
        {this.state.input.map((value, index) => {
          return (
            <input
              key='index'
              type='text'
              name={value}
              placeholder='Add new here...'
              onChange={this.handleInputChange}
            />
          );
        })}
      </form>
    );
  }
}
