import React, { Component } from 'react';
import InputComponent from '../Input';

export default class needs extends Component {
  constructor(props) {
    super(props);
    this.state = {
      name: 'teste',
      quantity: 0
    };
  }

  submitHandle = (event) => {
    event.preventDefault();
  };

  render() {
    return (
      <form onSubmit={this.submitHandle}>
        <InputComponent {...props} />;<button onClick={() => this.props.addResources()}>Add</button>
      </form>
    );
  }
}
