import React, { Component } from 'react';
import './styles.scss';

import { loadStripe } from '@stripe/stripe-js';
import { CardElement, Elements, ElementsConsumer } from '@stripe/react-stripe-js';

const STRIPE_PUBLIC_API_KEY = process.env.REACT_APP_STRIPE_API_PUBLIC_KEY;

const STRIPE_INPUT_OPTIONS = {
  style: {
    base: {
      fontSize: '16px',
      color: '#424770',
      fontFamily: 'sans-serif'
    },
    invalid: {
      color: '#c23d4b'
    }
  }
};
class ContributeMoneyView extends Component {
  constructor(props) {
    super(props);

    this.stripePromise = loadStripe(STRIPE_PUBLIC_API_KEY);
  }

  handleInputChange = (event) => {
    const { name, value } = event.target;
    this.setState({
      [name]: value
    });
  };
  render() {
    return (
      <div>
        <h1>Supporting</h1>
        <Elements stripe={this.stripePromise}>
          <ElementsConsumer>
            {({ stripe, elements }) => (
              <form onSubmit={(event) => this.handleFormSubmission(event, stripe, elements)}>
                <label htmlFor='address-input'>Delivery Address</label>
                <input
                  id='address-input'
                  type='text'
                  name='address'
                  placeholder='Full Address'
                  value={this.state.address}
                  onChange={this.handleInputChange}
                />

                <CardElement option={STRIPE_INPUT_OPTIONS} />

                <button>Confirm Purchase</button>
              </form>
            )}
          </ElementsConsumer>
        </Elements>
      </div>
    );
  }
}

export default ContributeMoneyView;
