import React, { Component } from 'react';
import './styles.scss';
import { updateProject, getProjectById } from './../../services/project';

class EditProjectView extends Component {
  constructor(props) {
    super(props);
    this.state = { project: null, loaded: false };
  }

  handleInputChange = ({ target: { name, value } }) => {
    // console.log(this.state.project);
    const newProject = { ...this.state.project };
    newProject[name] = value;
    this.setState({
      project: newProject
    });
  };

  loadSingleProject() {
    const id = this.props.match.params.project_id;
    getProjectById(id)
      .then((project) => {
        // console.log('project:', project);
        this.setState({
          loaded: true,
          project: { ...project }
        });
      })
      .catch((error) => {
        console.log(error);
      });
  }

  componentDidMount() {
    this.loadSingleProject();
  }

  handleFileInputChange = (event) => {
    const { name } = event.target;
    const file = event.target.files[0];
    const newProject = { ...this.state.project };
    newProject[name] = file;
    this.setState({
      project: newProject
    });
  };

  handleSubmit = (event) => {
    event.preventDefault();
    // console.log(this.state.project);
    const id = this.props.match.params.project_id;
    const { title, location, shortDescription, coverPictureUrl } = this.state.project;

    // console.log(title, location, shortDescription, coverPictureUrl);
    updateProject({ id, title, location, shortDescription, coverPictureUrl })
      .then((project) => {
        this.props.history.push(`/project/${id}`);
      })
      .catch((error) => {
        // console.log('This is the error');
        console.log(error);
      });
  };

  render() {
    const project = this.state.project;

    return (
      <div className='m-5'>
        {!this.state.loaded && (
          <>
            <span>Loading...</span>
          </>
        )}
        {project && (
          <form onSubmit={this.handleSubmit} className='w-full max-w-sm'>
            <div className='md:flex md:items-center mb-6'>
              <div className='md:w-1/3'>
                <label
                  htmlFor='title'
                  className='block text-gray-700 font-bold md:text-right mb-1 md:mb-0 pr-4'
                >
                  Title
                </label>
              </div>
              <div className='md:w-2/3'>
                <input
                  required
                  className='bg-gray-200 appearance-none border-2 border-gray-200 rounded w-full py-2 px-4 text-gray-700 leading-tight focus:outline-none focus:bg-white focus:border-purple-500" id="inline-full-name'
                  type='text'
                  className='update-title'
                  name='title'
                  value={project.title}
                  onChange={this.handleInputChange}
                />
              </div>
            </div>
            <div className='md:flex md:items-center mb-6'>
              <div className='md:w-1/3'>
                <label
                  className='block text-gray-700 font-bold md:text-right mb-1 md:mb-0 pr-4'
                  htmlFor='picture'
                >
                  Picture{' '}
                </label>
              </div>
              <div className='md:w-2/3'>
                <input
                  className='bg-gray-200 appearance-none border-2 border-gray-200 rounded w-full py-2 px-4 text-gray-700 leading-tight focus:outline-none focus:bg-white focus:border-purple-500" id="inline-full-name'
                  type='file'
                  name='coverPictureUrl'
                  id='coverPictureUrl'
                  onChange={this.handleFileInputChange}
                />
              </div>
            </div>
            <div className='md:flex md:items-center mb-6'>
              <div className='md:w-1/3'>
                <label
                  htmlFor='description'
                  className='block text-gray-700 font-bold md:text-right mb-1 md:mb-0 pr-4'
                >
                  Description
                </label>
              </div>
              <div className='md:w-2/3'>
                <input
                  required
                  className='bg-gray-200 appearance-none border-2 border-gray-200 rounded w-full py-2 px-4 text-gray-700 leading-tight focus:outline-none focus:bg-white focus:border-purple-500" id="inline-full-name'
                  type='text'
                  className='update-shortDescription'
                  name='shortDescription'
                  value={project.shortDescription}
                  onChange={this.handleInputChange}
                />
              </div>
            </div>
            {
              //----------
            }

            <label htmlFor='shortDescription'>Description</label>
            <input
              className='bg-gray-200 appearance-none border-2 border-gray-200 rounded w-full py-2 px-4 text-gray-700 leading-tight focus:outline-none focus:bg-white focus:border-purple-500" id="inline-full-name'
              type='text'
              className='update-shortDescription'
              name='shortDescription'
              value={project.shortDescription}
              onChange={this.handleInputChange}
            />

            <label htmlFor='location'>Location</label>
            <input
              className='bg-gray-200 appearance-none border-2 border-gray-200 rounded w-full py-2 px-4 text-gray-700 leading-tight focus:outline-none focus:bg-white focus:border-purple-500" id="inline-full-name'
              type='text'
              className='update-location'
              name='location'
              value={project.location}
              onChange={this.handleInputChange}
            />

            <label htmlFor='coverPictureUrl'>Cover Picture</label>
            <input
              className='bg-gray-200 appearance-none border-2 border-gray-200 rounded w-full py-2 px-4 text-gray-700 leading-tight focus:outline-none focus:bg-white focus:border-purple-500" id="inline-full-name'
              type='file'
              name='coverPictureUrl'
              id='coverPictureUrl'
              onChange={this.handleFileInputChange}
            />

            <button className='bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 border border-blue-700 rounded'>
              {' '}
              Submit{' '}
            </button>
          </form>
        )}
      </div>
    );
  }
}

export default EditProjectView;
