import React, { Component } from 'react';
import './styles.scss';

import { loadStripe } from '@stripe/stripe-js';
import { CardElement, Elements, ElementsConsumer } from '@stripe/react-stripe-js';

const STRIPE_PUBLIC_API_KEY =
  'pk_test_51GsFExGkeZVXbqieuChUHX3QEIELo1ei2BvwNcoxKaeokvcvJOXqFBaOwT9pqH9QIcXPMGYIEkK942xqD2TsUpmR00OF4rEBPS';

const STRIPE_INPUT_OPTIONS = {};
class ContributeMoneyView extends Component {
  constructor(props) {
    super(props);
    this.state = {
      contribution: 0
    };

    this.stripePromise = loadStripe(STRIPE_PUBLIC_API_KEY);
  }

  handleInputChange = (event) => {
    const { name, value } = event.target;
    this.setState({
      [name]: value
    });
  };
  render() {
    return (
      <div className='mt-12'>
        <Elements stripe={this.stripePromise}>
          <ElementsConsumer>
            {({ stripe, elements }) => (
              <form onSubmit={(event) => this.handleFormSubmission(event, stripe, elements)}>
                <label htmlFor='contribution'>Support with: </label>
                <input
                  type='number'
                  name='contribution'
                  id='contribution'
                  value={this.state.contribution}
                  onChange={this.handleInputChange}
                  style={{ textAlign: 'right', width: '80px' }}
                ></input>
                <label>â‚¬</label>

                <CardElement
                  option={{
                    style: {
                      base: {
                        fontSize: '50px',
                        color: '#424770',
                        fontFamily: 'sans-serif'
                      },
                      invalid: {
                        color: '#c23d4b'
                      }
                    }
                  }}
                />

                <button>Confirm Purchase</button>
              </form>
            )}
          </ElementsConsumer>
        </Elements>
      </div>
    );
  }
}

export default ContributeMoneyView;
