import React, { Component } from 'react';
import './styles.scss';

class ProfileUserView extends Component {
  constructor(props) {
    super(props);
    this.state = {
      showPictureForm: false
    };
  }

  changeThrutyForm = () => {
    console.log('entrou');
    let Thruty = !this.state.showPictureForm;
    this.setState = {
      showPictureForm: True
    };
  };

  render(props) {
    console.log(this.props.user);
    return (
      <div className=''>
        <img src={this.props.user.pictureUrl} alt='Profile' className='profile_img' />
        <button onClick={this.changeThrutyForm}>Change Picture</button>
        {this.state.showPictureForm && (
          <form>
            <input type='file' name='picture' id='picture'></input>
            <br></br>
            <button>Submit</button>
          </form>
        )}
        <br></br>
        <h1>{this.props.user.username}</h1>
        <br></br>
        <h2>Email: {this.props.user.email}</h2>
        <h2>Birthday: {this.props.user.birthday}</h2>
        <h2>Location: {this.props.user.location}</h2>
        <br></br>
        <p>My Projects</p>
        <br></br>
        <p>Contributed Actions</p>
      </div>
    );
  }
}

export default ProfileUserView;
