import React from 'react';
import { Link } from 'react-router-dom';
import './style.scss';
import formatDate from './../../helper/formatDate';
import percentageCalc from './../../helper/percentageCalculator';

const ProjectsList = (props) => {
  return (
    <div>
      {props.projects.map((project) => (
        <div key={project._id}>
          <div>
            <img src={project.coverPictureUrl} alt={project.title} />
            <div className='project_dateCreator'>
              <small>Created by: {project.creator.username}</small>
              <small>Date:{formatDate(project.updatedDate)}</small>
            </div>
          </div>
          <div className='project_information'>
            <h2>{project.title}</h2>
            <p>{project.shortDescription}</p>
            {project.needs.money.total && (
              <div className='relative pt-1 mr-16 ml-16'>
                <div className='overflow-hidden h-6 mb-4 text-xs flex rounded bg-indigo-200'>
                  <div
                    style={{ width: '50%' }}
                    className='shadow-none flex flex-col text-center whitespace-nowrap text-white justify-center bg-indigo-500'
                  >
                    <div className='flex mb-3 items-center justify-between align-center'>
                      <div>
                        <span className='text-small font-semibold inline-block text-white-600 mt-3 ml-3'>
                          0%
                        </span>
                      </div>
                      <div className='text-right'>
                        <span className='text-small font-semibold inline-block text-white-600 mt-3 mr-3'>
                          {percentageCalc(project.needs.money.backed, project.needs.money.total)}%
                        </span>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            )}
          </div>
          <div className='anchor_style'>
            <Link to={`/project/${project._id}`}>Read more here</Link>
          </div>
        </div>
      ))}
    </div>
  );
};

export default ProjectsList;
