import React, { Component } from 'react';

export default class needs extends Component {
  constructor(props) {
    super(props);
    this.state = {
      input: []
    };
  }

  clickAdd = (event) => {
    let newInput = {
      name: `input-${this.state.input.length}`,
      value: ''
    };
    this.setState((prevState) => ({ input: prevState.input.concat([newInput]) }));
  };

  handleInputChange = ({ target: { name, value } }) => {
    console.log(name, value);
    for (let input of this.state.input) {
      if (input.name === name) {
        this.setState({
          input: [{ [name]: value }]
        });
      }
    }
  };

  submitHandle = (event) => {
    event.preventDefault();
  };

  render() {
    return (
      <form onSubmit={this.submitHandle}>
        <button onClick={() => this.clickAdd()}>Add</button>
        {this.state.input.map((value) => {
          console.log(value);
          return (
            <input
              key={Math.random() * 100000}
              type='text'
              name={value.name}
              value={value.value}
              placeholder='Add new here...'
              onChange={this.handleInputChange}
            />
          );
        })}
      </form>
    );
  }
}
