import React, { Component } from 'react';
import './styles.scss';
import NeedsInput from './../../components/Input';
import { createProject } from './../../services/project';

export default class CreateProjectView extends Component {
  constructor() {
    super();
    this.state = {
      title: '',
      coverPictureUrl: null,
      description: '',
      category: 'Food',
      location: '',
      money: '',
      resources: [],
      volunteer: []
    };
  }

  addResource = (name, quantity) => {
    const arr = [...this.state.resources];
    const obj = { name, quantity };
    arr.push(obj);
    this.setState({
      resources: arr
    });
  };

  addVolunteer = (name, quantity) => {
    const arr = [...this.state.volunteer];
    const obj = { name, quantity };
    arr.push(obj);
    this.setState({
      volunteer: arr
    });
  };

  prevent = (event) => {
    event.preventDefault();
  };

  handleInputChange = ({ target: { name, value } }) => {
    console.log(name, value);
    this.setState({
      [name]: value
    });
  };

  handleFileInputChange = (event) => {
    const { name } = event.target;
    const file = event.target.files[0];
    this.setState({
      [name]: file
    });
  };

  deleteResource = (idx) => {
    const arr = [...this.state.resources];
    const newarr = arr.filter((resource, index) => idx !== index);
    this.setState({
      resources: newarr
    });
  };

  deleteVolunteer = (idx) => {
    const arr = [...this.state.volunteer];
    const newarr = arr.filter((v, index) => idx !== index);
    this.setState({
      volunteer: newarr
    });
  };

  create = (event) => {
    event.preventDefault();
    const data = { ...this.state };
    createProject(data)
      .then((result) => {
        console.log('result');
      })
      .then(() => {
        this.props.history.push('/');
      })
      .catch((err) => {
        console.log(err);
      });
  };

  render() {
    return (
      <div className='m-5'>
        <form className='w-full max-w-sm'>
          <div className='md:flex md:items-center mb-6'>
            <div className='md:w-1/3'>
              <label
                htmlFor='title'
                className='block text-gray-700 font-bold md:text-right mb-1 md:mb-0 pr-4'
              >
                Title
              </label>
            </div>
            <div class='md:w-2/3'>
              <input
                required
                className='bg-gray-200 appearance-none border-2 border-gray-200 rounded w-full py-2 px-4 text-gray-700 leading-tight focus:outline-none focus:bg-white focus:border-purple-500" id="inline-full-name'
                id='title'
                type='text'
                name='title'
                placeholder='Insert title here'
                onChange={this.handleInputChange}
              />
            </div>
          </div>
          <div className='md:flex md:items-center mb-6'>
            <div className='md:w-1/3'>
              <label
                className='block text-gray-700 font-bold md:text-right mb-1 md:mb-0 pr-4'
                htmlFor='picture'
              >
                Picture{' '}
              </label>
            </div>
            <div class='md:w-2/3'>
              <input
                required
                type='file'
                name='coverPictureUrl'
                id='picture'
                onChange={this.handleFileInputChange}
              />
            </div>
          </div>

          <div className='md:flex md:items-center mb-6'>
            <div className='md:w-1/3'>
              <label
                htmlFor='description'
                className='block text-gray-700 font-bold md:text-right mb-1 md:mb-0 pr-4'
              >
                Description
              </label>
            </div>
            <div class='md:w-2/3'>
              <input
                required
                className='bg-gray-200 appearance-none border-2 border-gray-200 rounded w-full py-2 px-4 text-gray-700 leading-tight focus:outline-none focus:bg-white focus:border-purple-500" id="inline-full-name'
                id='description'
                type='text'
                name='description'
                placeholder='Insert description here'
                onChange={this.handleInputChange}
              />
            </div>
          </div>

          <div className='md:flex md:items-center mb-6'>
            <div className='md:w-1/3'>
              <label
                htmlFor='location'
                className='block text-gray-700 font-bold md:text-right mb-1 md:mb-0 pr-4'
              >
                Location
              </label>
            </div>
            <div class='md:w-2/3'>
              <input
                required
                className='bg-gray-200 appearance-none border-2 border-gray-200 rounded w-full py-2 px-4 text-gray-700 leading-tight focus:outline-none focus:bg-white focus:border-purple-500" id="inline-full-name'
                id='location'
                type='text'
                name='location'
                placeholder='Insert location here'
                onChange={this.handleInputChange}
              />
            </div>
          </div>

          <div className='md:flex md:items-center mb-6'>
            <div className='md:w-1/3'>
              <label
                htmlFor='category'
                className='block text-gray-700 font-bold md:text-right mb-1 md:mb-0 pr-4'
              >
                Category:
              </label>
            </div>
            <div class='md:w-2/3'>
              <div class='inline-block relative w-64'>
                <select
                  id='category'
                  name='category'
                  onChange={this.handleInputChange}
                  class='block appearance-none w-full bg-white border border-gray-400 hover:border-gray-500 px-4 py-2 pr-8 rounded shadow leading-tight focus:outline-none focus:shadow-outline'
                >
                  <option value='Food'>Food</option>
                  <option value='Education'>Education</option>
                  <option value='Environment'>Environment</option>
                  <option value='Sanitation'>Sanitation</option>
                  <option value='Human Rights'>Human Rights</option>
                  <option value='Donation'>Donation</option>
                  <option value='Other'>Other</option>
                </select>
                <div class='pointer-events-none absolute inset-y-0 right-0 flex items-center px-2 text-gray-700'>
                  <svg
                    class='fill-current h-4 w-4'
                    xmlns='http://www.w3.org/2000/svg'
                    viewBox='0 0 20 20'
                  >
                    <path d='M9.293 12.95l.707.707L15.657 8l-1.414-1.414L10 10.828 5.757 6.586 4.343 8z' />
                  </svg>
                </div>
              </div>
            </div>
          </div>

          <h1>Needs: </h1>

          <div className='md:flex md:items-center mb-6'>
            <div className='md:w-1/3'>
              <label
                htmlFor='money'
                className='block text-gray-700 font-bold md:text-right mb-1 md:mb-0 pr-4'
              >
                Money
              </label>
            </div>
            <div class='md:w-2/3 flex flex-row'>
              <input
                min='0'
                className='bg-gray-200 appearance-none border-2 border-gray-200 rounded w-full py-2 px-4 text-gray-700 leading-tight focus:outline-none focus:bg-white focus:border-purple-500" id="inline-full-name'
                id='money'
                type='number'
                name='money'
                placeholder='insert amount'
                onChange={this.handleInputChange}
              />
              <p>â‚¬</p>
            </div>
          </div>
        </form>
        <div>
          <label className='block text-gray-700 font-bold md:text-right mb-1 md:mb-0 pr-4'>
            Resources
          </label>

          <NeedsInput addResources={this.addResource} />
          <div className='mb-5 mt-3'>
            {this.state.resources.map((resource, index) => {
              return (
                <div key={index} style={{ display: 'flex' }}>
                  <p>
                    {resource.quantity} {resource.name}
                  </p>
                  <form onSubmit={this.prevent}>
                    <button onClick={() => this.deleteResource(index)} className='delete-btn'>
                      <p>X</p>
                    </button>
                  </form>
                </div>
              );
            })}
          </div>

          <label className='block text-gray-700 font-bold md:text-right mb-1 md:mb-0 pr-4'>
            Volunteer
          </label>
          <NeedsInput addResources={this.addVolunteer} />

          {this.state.volunteer.map((volunteer, index) => {
            return (
              <div key={index} style={{ display: 'flex' }}>
                <p>
                  {volunteer.quantity} {volunteer.name}
                </p>
                <form onSubmit={this.prevent}>
                  <button onClick={() => this.deleteVolunteer(index)} className='delete-btn'>
                    X
                  </button>
                </form>
              </div>
            );
          })}
        </div>
        <form className='mt-12 text-center' onSubmit={this.create}>
          <button className='text-center bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 border border-blue-700 rounded'>
            Create Project
          </button>
        </form>
      </div>
    );
  }
}
