import React, { Component } from 'react';

export default class needs extends Component {
  constructor(props) {
    super(props);
    this.state = {
      input: []
    };
  }

  clickAdd = (event) => {
    let newInput = {
      name: `input-${this.state.input.length}`,
      value: ''
    };
    this.setState((prevState) => ({ input: prevState.input.concat([newInput]) }));
  };

  handleInputChange = ({ target: { name, value } }) => {
    console.log(name, value);
    const cloneInput = [...this.state.input];
    let objecto = {};
    let resultado = cloneInput.map((input) => {
      if (input.name === name) {
        objecto = { nome: input.name, value: value };
        return objecto;
      } else {
        objecto = { nome: input.name, value: input.value };
        return objecto;
      }
    });
    console.log(resultado);

    this.setState({
      input: [resultado]
    });
  };

  submitHandle = (event) => {
    event.preventDefault();
  };

  render() {
    return (
      <form onSubmit={this.submitHandle}>
        <button onClick={() => this.clickAdd()}>Add</button>
        {this.state.input.map((value) => {
          console.log(value);
          return (
            <input
              key={Math.random() * 100000}
              type='text'
              name={value.name}
              value={value.value}
              placeholder='Add new here...'
              onChange={this.handleInputChange}
            />
          );
        })}
      </form>
    );
  }
}
