  }

  componentDidMount() {
    let factLength = this.state.fact.length;
    let randomShow = 0;
    this.myInterval = setInterval(() => {
      randomShow = Math.floor(Math.random() * factLength);
      console.log(randomShow);

      this.setState = {
        randomShow: this.randomShow
      };
    }, 1000);
  }

  componentWillUnmount() {
    clearInterval(this.myInterval);
  }

  render() {
    const { randomShow } = this.state;
    console.log(randomShow);
    return (
      <div>
        <h1>Current Count: {this.state.fact[this.state.randomShow]}</h1>
      </div>
    );
  }
}

export default Fact;
