import React, { Component } from 'react';
import './styles.scss';
import Needs from './../../components/Needs';

export default function CreateProjectView() {
  handleInputChange = ({ target: { name, value } }) => {
    this.setState({
      [name]: value
    });
  };

  return (
    <form>
      <label htmlFor='title'>Title</label>
      <input
        id='title'
        type='text'
        name='title'
        placeholder='Insert title here'
        onChange={handleInputChange()}
      />

      <label htmlFor='pictureUrl'>Picture</label>
      <input type='file' name='pictureUrl' id='pictureUrl' onChange={handleInputChange()} />

      <label htmlFor='description'>Description</label>
      <input
        id='description'
        type='text'
        name='description'
        placeholder='Insert description here'
        onChange={handleInputChange()}
      />

      <label htmlFor='location'>Description</label>
      <input
        id='location'
        type='location'
        name='location'
        placeholder='Insert location here'
        onChange={handleInputChange()}
      />

      <label htmlFor='needs'>Needs:</label>
      <input type='number' id='money' name='money' value='money' onChange={handleInputChange()} />
      <label>Resources</label>
      {<Needs />}
      <label>Volunteer</label>
      {<Needs />}
      <button>Submit</button>
    </form>
  );
}
